pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract BasePolkaOffChain is Ownable {
    using Counters for Counters.Counter;

    event BuyProduct(uint256 indexed _productId, address _buyer);
    event SetExchangeAgent(address _setter, address _exchangeAgent);

    Counters.Counter public productIds;
    mapping(uint256 => address) private _ownerOf; // productId => owner
    mapping(address => uint64) private _balanceOf; // owner => balance We can think one user can buy max 2**64 products
    mapping(address => uint64[]) private _productsOf; // owner => productIds[]

    // TODO it should be EnumerableSet
    mapping(address => bool) public availableCurrencies;

    address public immutable WETH;
    // TODO should it be public?
    address public exchangeAgent;
    address public devWallet;

    constructor(
        address _WETH,
        address _exchangeAgent,
        address _devWallet
    ) {
        WETH = _WETH;
        exchangeAgent = _exchangeAgent;
        devWallet = _devWallet;
    }

    modifier onlyAvailableToken(address _token) {
        require(availableCurrencies[_token], "Not allowed token");
        _;
    }

    receive() external payable {}

    function setExchangeAgent(address _exchangeAgent) external onlyOwner {
        require(_exchangeAgent != address(0), "ZERO Address");
        exchangeAgent = _exchangeAgent;
        emit SetExchangeAgent(msg.sender, _exchangeAgent);
    }

    function _setProductOwner(uint256 _prodId, address _owner) internal {
        _ownerOf[_prodId] = _owner;
    }

    function ownerOf(uint256 _prodId) public view returns (address) {
        require(_prodId < productIds.current() + 1, "Invalid product ID");
        return _ownerOf[_prodId];
    }

    function _increaseBalance(address _account) internal {
        _balanceOf[_account]++;
    }

    function balanceOf(address _account) public view returns (uint64) {
        return _balanceOf[_account];
    }

    function _buyProduct(address _buyer, uint256 _pid) internal {
        _productsOf[_buyer].push(uint64(_pid));
        emit BuyProduct(_pid, _buyer);
    }

    function productOf(address _owner, uint64 _idx) public view returns (uint64) {
        return _productsOf[_owner][_idx];
    }

    function addCurrency(address _currency) external onlyOwner {
        require(!availableCurrencies[_currency], "Already available");
        availableCurrencies[_currency] = true;
    }

    function removeCurrency(address _currency) external onlyOwner {
        require(availableCurrencies[_currency], "Not available yet");
        availableCurrencies[_currency] = false;
    }

    function permit(
        address _sender,
        bytes32 _digest,
        bytes memory sig
    ) internal pure virtual {
        (bytes32 r, bytes32 s, uint8 v) = splitSignature(sig);
        address recoveredAddress = ecrecover(_digest, v, r, s);
        require(recoveredAddress != address(0) && recoveredAddress == _sender, "PolkaCompare: INVALID_SIGNATURE");
    }

    function splitSignature(bytes memory sig)
        public
        pure
        returns (
            bytes32 r,
            bytes32 s,
            uint8 v
        )
    {
        require(sig.length == 65, "invalid signature length");

        assembly {
            // first 32 bytes, after the length prefix
            r := mload(add(sig, 32))
            // second 32 bytes
            s := mload(add(sig, 64))
            // final byte (first byte of the next 32 bytes)
            v := byte(0, mload(add(sig, 96)))
        }

        // implicitly return (r, s, v)
    }
}
","keccak256":"0x065424a2224e5f85a0548d2f24046413c105002afc406ca20bbbe7d96ff0b77e","license":"MIT"},"contracts/offchain/P4LPolka.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "../interfaces/IExchangeAgent.sol";
import "../libs/TransferHelper.sol";
import "./BasePolkaOffChain.sol";
import "hardhat/console.sol";

contract P4LPolka is Ownable, ReentrancyGuard, BasePolkaOffChain {
    event BuyP4L(uint256 indexed _productId, address _buyer, address _currency, uint256 _amount, uint256 _priceInUSD);

    using Counters for Counters.Counter;

    struct Product {
        uint256 startTime;
        uint128 priceInUSD; // price in USD
        uint128 durPlan;
        uint64 purchMonth;
        string device;
        string brand;
    }

    mapping(uint256 => Product) public products; // productId => product

    constructor(
        address _WETH,
        address _exchangeAgent,
        address _devWallet
    ) BasePolkaOffChain(_WETH, _exchangeAgent, _devWallet) {}

    /**
     * @dev buyProductETH function:
     * this function should be called from user directly
     */
    function buyProductByETH(
        string memory _device,
        string memory _brand,
        uint256 _value, // price in USD
        uint256 _purchMonth,
        uint256 _durPlan,
        bytes memory sig
    ) external payable nonReentrant {
        bytes32 digest = getSignedMsgHash(_device, _brand, _value, _purchMonth, _durPlan);
        permit(msg.sender, digest, sig);
        uint256 tokenAmount = IExchangeAgent(exchangeAgent).getETHAmountForUSDC(_value);

        require(msg.value >= tokenAmount, "Insufficient amount");
        if (msg.value > tokenAmount) {
            TransferHelper.safeTransferETH(msg.sender, msg.value - tokenAmount);
        }
        TransferHelper.safeTransferETH(devWallet, tokenAmount);

        uint256 _pid = buyProduct(uint128(_value), uint128(_durPlan), uint64(_purchMonth), _device, _brand, msg.sender);
        emit BuyP4L(_pid, msg.sender, WETH, tokenAmount, _value);
    }

    /**
     * @dev buyProductByToken: Users can buy products using ERC20 tokens such as CVR and without gas fee
     */
    function buyProductByToken(
        string memory _device,
        string memory _brand,
        uint256 _value,
        uint256 _purchMonth,
        uint256 _durPlan,
        address _token,
        address _sender,
        bytes memory sig
    ) external nonReentrant onlyAvailableToken(_token) {
        bytes32 digest = getSignedMsgHash(_device, _brand, _value, _purchMonth, _durPlan);
        permit(_sender, digest, sig);

        uint256 tokenAmount = IExchangeAgent(exchangeAgent).getTokenAmountForUSDC(_token, _value);
        TransferHelper.safeTransferFrom(_token, _sender, devWallet, tokenAmount);
        uint256 _pid = buyProduct(uint128(_value), uint128(_durPlan), uint64(_purchMonth), _device, _brand, _sender);
        emit BuyP4L(_pid, _sender, _token, tokenAmount, _value);
    }

    function buyProduct(
        uint128 _value,
        uint128 _durPlan,
        uint64 _purchMonth,
        string memory _device,
        string memory _brand,
        address _sender
    ) private returns (uint256 _pid) {
        _pid = productIds.current();
        products[_pid] = Product({
            priceInUSD: _value, // price in USD
            purchMonth: _purchMonth,
            durPlan: _durPlan,
            startTime: block.timestamp,
            device: _device,
            brand: _brand
        });
        _setProductOwner(_pid, _sender);
        _increaseBalance(_sender);
        _buyProduct(_sender, _pid);
        productIds.increment();
    }

    function getSignedMsgHash(
        string memory _device,
        string memory _brand,
        uint256 _value,
        uint256 _purchMonth,
        uint256 _durPlan
    ) internal pure returns (bytes32) {
        bytes32 msgHash = keccak256(abi.encodePacked(_device, _brand, _value, _purchMonth, _durPlan));
        /*
        Signature is produced by signing a keccak256 hash with the following format:
        "\x19Ethereum Signed Message\n" + len(msg) + msg
        */
        return keccak256(abi.encodePacked("\x19Ethereum Signed Message:\n32", msgHash));
    }
}
